function retourInfoPlanchette(idPlanchette){
    var i = 0;
    for(i = 0; i < tableADPlanchette.length; i++){
        if(tableADPlanchette[i][0] == idPlanchette){
            return tableADPlanchette[i][1];
        }
    }
}

