var lettre = ['A','B','C','D','E','F','G','H','I','J','K','L'];

module.exports = {
    retourneLettre: function(i){
        return lettre[(parseInt(i)-1)];
    },

    retourneIndex: function(l){
        var err = -1;
        var i = 0;
        for(i = 0; i < lettre.length; i++){
            if(lettre[i] === l){
                return i;
            }
        }
        return err;
    }
}