function mov6EqNbRondes(table, nbR){
    switch(parseInt(nbR)) {
        case 1:
            return mouvements[0][table];
        case 2:
            return mouvements[1][table];
        default:
            return [];
    }

}

function mov6Equipes5Rondes(){
    /*table 1*/
    var mov = [6,1,[1]];
    var movTab = [];
    var movTsble = [];
    movTab.push(mov);
    mov = [6,2,[2]];
    movTab.push(mov);
    mov = [6,3,[3]];
    movTab.push(mov);
    mov = [6,4,[4]];
    movTab.push(mov);
    mov = [6,5,[5]];
    movTab.push(mov);
    movTsble.push(movTab);
    movTab = [];
    /*table 2*/
    mov = [4,3,[2]];
    movTab.push(mov);
    mov = [5,4,[3]];
    movTab.push(mov);
    mov = [1,5,[2]];
    movTab.push(mov);
    mov = [2,1,[3]];
    movTab.push(mov);
    mov = [3,2,[5]];
    movTab.push(mov);
    movTsble.push(movTab);
    movTab = [];
    /*table 3*/
    mov = [2,5,[4]];
    movTab.push(mov);
    mov = [3,1,[4]];
    movTab.push(mov);
    mov = [4,2,[1]];
    movTab.push(mov);
    mov = [5,3,[1]];
    movTab.push(mov);
    mov = [1,4,[5]];
    movTab.push(mov);
    movTsble.push(movTab);


    mouvements.push(movTsble);

}

