var fs = require('fs');



module.exports = {

    ajouteNom: function(nom){
        var liste = readListe();

        liste.push(nom);

        var objStr = JSON.stringify(liste);

        fs.writeFileSync('./fichiers/nomsDesJoueurs.txt', objStr, 'utf8');
    },

    listeNoms: function(){

        return readListe().sort();
    }
}

function readListe(){
    var content = fs.readFileSync('./fichiers/nomsDesJoueurs.txt', 'utf8');
    return(JSON.parse(content));
}